example.code:
//
// Lambda style
//

// listening
notification = AVCaptureSession.Notifications.ObserveObserveDidStartRunning ((sender, args) => {
    /* Access strongly typed args */
    Console.WriteLine ("Notification: {0}", args.Notification);
});

// To stop listening:
notification.Dispose ();

//
//Method style
//
NSObject notification;
void Callback (object sender, ObserveDidStartRunning args)
{
    // Access strongly typed args
    Console.WriteLine ("Notification: {0}", args.Notification);
}

void Setup ()
{
    notification = AVCaptureSession.Notifications.ObserveObserveDidStartRunning (Callback);
}

void Teardown ()
{
    notification.Dispose ();
}

