// Lambda style
NSNotificationCenter.DefaultCenter.AddObserver (
        NSPopover.DidCloseNotification, (notification) => {Console.WriteLine ("Received the notification NSPopover", notification); }


// Method style
void Callback (NSNotification notification)
{
    Console.WriteLine ("Received a notification NSPopover", notification);
}

void Setup ()
{
    NSNotificationCenter.DefaultCenter.AddObserver (NSPopover.DidCloseNotification, Callback);
}

// Lambda style
